INSERT INTO `xsmart_news`(`id`,`catid`,`title`,`style`,`thumb`,`keywords`,`description`,`content`,`islink`,`url`,`listorder`,`ontop`,`recommend`,`audit`,`username`,`updatetime`,`inputtime`) VALUES ("167","9","#学啊网#大连飞创信息技术有限公司招聘C++高级开发工程师","","","学啊网 学啊互联 在线教育 职业教育","职位月薪：面议\r\n工作地点：北京\r\n发布日期：2015-06-10\r\n工作性质：全职\r\n工作经验：无经验\r\n最低学历：本科\r\n招聘人数：5人\r\n职位类别：高级软件工程师","<p><span style=\\\"font-size:20px;\\\">#<a href=\\\"http://www.xuer.com/\\\" target=\\\"_blank\\\" title=\\\"学啊网\\\">学啊网</a>#大连飞创信息技术有限公司北京分公司招聘C++高级开发工程师</span><br /></p><p>职位月薪：面议</p><p>工作地点：北京</p><p>发布日期：2015-06-10</p><p>工作性质：全职</p><p>工作经验：无经验</p><p>最低学历：本科</p><p>招聘人数：5人</p><p>职位类别：高级软件工程师</p><p>岗位职责：</p><p>1、按照有关制度和规范的要求，参与项目和产品的设计（功能设计、数据结构设计、系统架构设计、数据库设计）、编写设计文档，并制定详细开发计划；</p><p>2、根据项目计划参与编码工作，并主要承担核心模块的编码工作。对产品需求、业务知识理解透彻。按照公司要求，保证各项成果提交的规范性、及时性。积极配合测试团队开展集成测试工作；</p><p>3、前沿技术研究，难点攻关。可以承担内部技术培训工作；可以承担客户业务、技术培训；</p><p>4、必要时对项目组内初级别开发人员进行技术指导，监控代码开发质量，对代码进行评审。制定开发规范；</p><p>5、负责产品或系统架构设计工作，或参与架构设计评审。</p><p>技术要求：</p><p>1、IT方面学士或学士以上学历；</p><p>2、五年以上IT行业工作经验；</p><p>3、熟练掌握C/C++编程， 基本功扎实；</p><p>4、精通STL、boost、apr、ACE库中的一种或常用数据结构、算法；</p><p>5、精通TCP/IP协议，熟练掌握Socket编程，对IOCP、Poll、Epoll网络模型有一定了解；</p><p>6、熟练掌握进程间通信、多线程编程等开发技术，对消息队列、共享内存、线程并发控制有深入理解；</p><p>7、熟练操作Linux系统，熟练掌握Linux C/C++调试、开发工具gcc，g++，gdb，make；</p><p>8、熟练掌握Windows图形界面开发技术MFC或WTL，熟练使用开发工具VisualC++。<br />工作地址：北京市朝阳区裕民路12号中国国际科技会展中心A座10层A1011号</p><p>更多招聘信息请关注<a href=\\\"http://www.xuer.com\\\" target=\\\"_blank\\\" title=\\\"学啊网\\\">学啊网</a></p>","0","","1","1","1","1","","1433907341","1433907341");
INSERT INTO `xsmart_news`(`id`,`catid`,`title`,`style`,`thumb`,`keywords`,`description`,`content`,`islink`,`url`,`listorder`,`ontop`,`recommend`,`audit`,`username`,`updatetime`,`inputtime`) VALUES ("168","16","#学啊网#北京天航信民航通信网络发展有限公司招聘java开发","","","学啊网 学啊互联 在线教育 职业教育","职位月薪：面议\r\n工作地点：北京-海淀区\r\n发布日期：2015-06-10\r\n工作性质：全职\r\n工作经验：不限\r\n最低学历：本科\r\n招聘人数：1人\r\n职位类别：软件工程师","<p><span style=\\\"font-size:24px;\\\">#<a href=\\\"http://www.xuer.com/\\\" target=\\\"_blank\\\" title=\\\"学啊网\\\">学啊网</a>#北京天航信民航通信网络发展有限公司招聘java开发工程师</span><br /></p><p>职位月薪：面议</p><p>工作地点：北京-海淀区</p><p>发布日期：2015-06-10</p><p>工作性质：全职</p><p>工作经验：不限</p><p>最低学历：本科</p><p>招聘人数：1人</p><p>职位类别：软件工程师</p><p>岗位职责：<br />1、参与项目前期技术调研、需求调研工作<br />2、参与项目的系统架构设计、数据库设计<br />3、负责软件功能模块的模块设计、编码开发、单元测试、Bug修复<br />4、编写软件设计开发文档<br />5、协助编写产品宣传材料，参与产品售前技术支持 </p><p>任职要求：<br />1、计算机或相关专业，大学本科或以上学历；<br />2、两年以上J2EE项目开发经验，有大型项目开发经验；<br />3、熟悉MVC模式，熟悉struts，spring，hibernate等开源框架，有spring+hibernate编程经验优先；<br />4、熟悉JSTL/javascript/ajax等WEB技术；<br />5、熟悉SQL Server，oracle数据库，有数据库设计经验者优先；<br />6、熟悉web应用服务器，如tomcat并能进行相应的操作和配置；<br />7、了解路由器，交换机等相关网络知识，有网络监控系统开发经验者优先；<br />8、熟悉Android开发平台及框架原理，具备Android开发经验者优先；<br />9、有团队合作精神和良好的沟通能力及心理素质，责任心强。<br />工作地址：北京海淀区学清路8号科技财富中心B座12层</p><p>更多招聘信息请关注<a href=\\\"http://www.xuer.com\\\" target=\\\"_blank\\\" title=\\\"学啊网\\\">学啊网</a></p>","0","","1","1","1","1","","1433907665","1433907665");
