INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("22","0","3","","/var/upload/image/2015/05/20150501113608_17553.png","","","","0","1","0","","","",""),("23","0","3","","/var/upload/image/2015/05/20150501113608_17553.png","","","","0","1","0","","","",""),("24","0","3","","/var/upload/image/2015/05/20150501113608_17553.png","","","","0","1","0","","","",""),("25","0","3","","/var/upload/image/2015/05/20150501113608_17553.png","","","","0","1","0","","","",""),("31","21","0","河南科技大学","/var/upload/image/2015/05/20150527152940_36691.jpg","http://","简介","2015-05-18 17:58:16","0","1","0","0","24","","");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("37","22","0","河南科技大学","/var/upload/image/2015/05/20150526164327_35573.png","http://","         河南科技大学（Henan University of Science and Technology，简称：河科大）是首批卓越医生教育培养计划重点建设大学，第二批卓越工程师教育培养计划重点建设大学，是原中华人民共和国农业机械部和原中华人民共和国机械工业部直属的中央部属高校，是河南省重点支持建设的第三所综合性大学[1] 。\r\n         河南科技大学坐落在千年帝都、牡丹花城、现代化工业城市——洛阳，截止2013年10月，学校有西苑、景华、周山、开元等四个校区，占地面积4600余亩，校 舍建筑面积151万平方米,其中专业实验室面积32.7万平方米。固定资产总值21.5亿元，教学科研仪器设备总值3.7亿元。图书馆建筑面积9.9万平方米，馆藏文献361.8万册，中外文期刊近2200多种，18万册。学校数字化校园建设和应用水平达到国内先进水平，荣获中国教育信息化建设优秀奖。体育场馆面积17万平方米。校内外实习基地200余个。[1] ","2015-05-19 10:52:14","0","1","0","0","24","","<p> &nbsp; 河南科技大学（Henan University of Science and Technology，简称：河科大）是首批卓越医生教育培养计划重点建设大学，第二批卓越工程师教育培养计划重点建设大学，是原中华人民共和国农业机械部和原中华人民共和国机械工业部直属的中央部属高校，是河南省重点支持建设的第三所综合性大学[1] 。<br /> &nbsp; &nbsp; &nbsp; &nbsp;河南科技大学坐落在千年帝都、牡丹花城、现代化工业城市——洛阳，截止2013年10月，学校有西苑、景华、周山、开元等四个校区，占地面积4600余亩，校 舍建筑面积151万平方米,其中专业实验室面积32.7万平方米。固定资产总值21.5亿元，教学科研仪器设备总值3.7亿元。图书馆建筑面积9.9万平方米，馆藏文献361.8万册，中外文期刊近2200多种，18万册。学校数字化校园建设和应用水平达到国内先进水平，荣获中国教育信息化建设优秀奖。体育场馆面积17万平方米。校内外实习基地200余个。<br /></p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("38","22","0","洛阳理工学院","/var/upload/image/2015/05/20150526164247_34826.png","http://","        洛阳理工学院（Luoyang Institute of Science and Technology）坐落于 素有“千年帝都，牡丹花城”美誉的历史文化名城洛阳。学校南与世界文化遗产龙门石窟毗邻，北与风景如画的洛浦公园相连。\r\n        学校始建于1956年，是一所以工学为主，兼有理学、管理学、文学、经济学、法学、教育学、艺术学等多种学科的省属普通本科院校。\r\n截至到2014，学校有王城、开元、九都三个校区，占地面积2229亩，学院有全日制普通在校生2.6万余人，共设置17个系（部）和国际教育学院、示范性软件职业技术学院、继续教育学院、天瑞干部学院、中迈干部管理学院、洛阳市服务外包学院等6个专门教育学院，本科专业37个。\r\n        截至到2014，学院承担了国家自然科学基金项目、国家社会科学基金项目、863计划、973项目、十二五国家科技支撑计划项目、省杰出青年基金等省级以上科研项目共计344余项, 荣获省部级以上科研成果奖25项，学院荣获省级教学成果一等奖8项，获得国家专利授权222项。","2015-05-19 11:06:11","0","1","0","0","25","","<p> &nbsp; 洛阳理工学院（Luoyang Institute of Science and Technology）坐落于 素有“千年帝都，牡丹花城”美誉的历史文化名城洛阳。学校南与世界文化遗产龙门石窟毗邻，北与风景如画的洛浦公园相连。</p><p> &nbsp; 学校始建于1956年，是一所以工学为主，兼有理学、管理学、文学、经济学、法学、教育学、艺术学等多种学科的省属普通本科院校。</p><p>截至到2014，学校有王城、开元、九都三个校区，占地面积2229亩，学院有全日制普通在校生2.6万余人，共设置17个系（部）和国际教育学院、示范性软件职业技术学院、继续教育学院、天瑞干部学院、中迈干部管理学院、洛阳市服务外包学院等6个专门教育学院，本科专业37个。</p><p> &nbsp; 截至到2014，学院承担了国家自然科学基金项目、国家社会科学基金项目、863计划、973项目、十二五国家科技支撑计划项目、省杰出青年基金等省级以上科研项目共计344余项, 荣获省部级以上科研成果奖25项，学院荣获省级教学成果一等奖8项，获得国家专利授权222项。</p><p><br /></p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("39","22","0","洛阳师范学院","/var/upload/image/2015/05/20150526164213_71611.png","http://","        洛阳师范学院是一所省属普通高等师范本科院校，位于千年帝都、牡丹花城——洛阳。学校的前身是始建于1916年的河洛道师范学校，历经河南省立河洛道师范学校、河南省立第四师范学校、河南省立洛阳师范学校、洛阳师范专科学校、洛阳师范高等专科学校诸阶段，2000年升格为本科院校更名洛阳师范学院。2007年，学校以优异成绩通过教育部本科教学评估，2011年获批教育硕士专业学位研究生培养试点单位[1] 。\r\n        截至2014年， 学校现有文学院、历史文化学院、政法与公共管理学院、外国语学院、数学科学学院、物理与电子信息学院、化学化工学院、信息技术学院、体育学院、音乐学院、美术学院、教育科学学院、生命科学学院、商学院、学前教育学院、新闻与传播学院、国土与旅游学院、艺术设计学院、国际教育学院、继续教育学院、软件职业技术学院21个学院，4个公共教研部，61个本科专业，涵盖文学、理学、工学、经济学、管理学、法学、历史学、教育学、艺术学、农学等10大学科门类。学校有全日制在校生28000余人，各类成人教育学生4000余人。","2015-05-19 11:07:23","0","1","0","0","105","","<p> 洛阳师范学院是一所省属普通高等师范本科院校，位于千年帝都、牡丹花城——洛阳。学校的前身是始建于1916年的河洛道师范学校，历经河南省立河洛道师范学校、河南省立第四师范学校、河南省立洛阳师范学校、洛阳师范专科学校、洛阳师范高等专科学校诸阶段，2000年升格为本科院校更名洛阳师范学院。2007年，学校以优异成绩通过教育部本科教学评估，2011年获批教育硕士专业学位研究生培养试点单位[1] 。<br /> &nbsp; &nbsp; &nbsp; &nbsp;截至2014年， 学校现有文学院、历史文化学院、政法与公共管理学院、外国语学院、数学科学学院、物理与电子信息学院、化学化工学院、信息技术学院、体育学院、音乐学院、美术学院、教育科学学院、生命科学学院、商学院、学前教育学院、新闻与传播学院、国土与旅游学院、艺术设计学院、国际教育学院、继续教育学院、软件职业技术学院21个学院，4个公共教研部，61个本科专业，涵盖文学、理学、工学、经济学、管理学、法学、历史学、教育学、艺术学、农学等10大学科门类。学校有全日制在校生28000余人，各类成人教育学生4000余人。<br /></p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("40","22","0","许昌学院","/var/upload/image/2015/05/20150526164117_78788.png","http://","        许昌学院（Xuchang University）是一所省属全日制普通本科院校。学校坐落于汉魏故都——河南省许昌市。学校的前身是创建于1942年8月的河南省第五行政区联立师范学校，2002年3月升格为本科院校，学校在纳米材料、应用化学、电气信息等方面具有领先水平，并具有留学生招生资格。2013年被确定为河南省“地方本科高校转型发展试点单位”。在2014中国大学排名中位居第400位。[1-2]   \r\n        截止2014年10月，学校校园占地近1700亩，建筑面积60万平方米，教学仪器设备总值1.82亿元，馆藏纸质图书172.6万册，有教职工1301人，设有25个教学院部，举办有57个本科专业，有全日制在校生2.4万人。[3] ","2015-05-19 11:08:29","0","1","0","0","107","","<p> &nbsp;许昌学院（Xuchang University）是一所省属全日制普通本科院校。学校坐落于汉魏故都——河南省许昌市。学校的前身是创建于1942年8月的河南省第五行政区联立师范学校，2002年3月升格为本科院校，学校在纳米材料、应用化学、电气信息等方面具有领先水平，并具有留学生招生资格。2013年被确定为河南省“地方本科高校转型发展试点单位”。在2014中国大学排名中位居第400位。[1-2] &nbsp; <br /> &nbsp; &nbsp; &nbsp; &nbsp;截止2014年10月，学校校园占地近1700亩，建筑面积60万平方米，教学仪器设备总值1.82亿元，馆藏纸质图书172.6万册，有教职工1301人，设有25个教学院部，举办有57个本科专业，有全日制在校生2.4万人。[3] <br /><br /></p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("41","22","0","郑州科技学院","/var/upload/image/2015/05/20150526164040_63333.png","http://","        郑州科技学院创建于1988年，原名为中原职业大学，属于高等教育自学考试辅导为主的高等教育机构；1997年实施高等教育学历文凭考试；先后经历了郑州科技专修学院、郑州科技职业学院等时期，于2008年升格为本科院校，并更名为郑州科技学院。\r\n        截至2013年11月，该校占地1264亩，建筑面积43万m2 ，教学仪器设备总值9000万元，馆藏图书133万册。有专兼职教师近千人。开设本科专业28个，专科专业32个，对外合作办学专业3个，各类在校生17000余人（含中专及五年制大专）[1] 。","2015-05-19 11:09:50","0","1","0","0","108","","<p> &nbsp;郑州科技学院创建于1988年，原名为中原职业大学，属于高等教育自学考试辅导为主的高等教育机构；1997年实施高等教育学历文凭考试；先后经历了郑州科技专修学院、郑州科技职业学院等时期，于2008年升格为本科院校，并更名为郑州科技学院。</p><p> &nbsp; 截至2013年11月，该校占地1264亩，建筑面积43万m2 ，教学仪器设备总值9000万元，馆藏图书133万册。有专兼职教师近千人。开设本科专业28个，专科专业32个，对外合作办学专业3个，各类在校生17000余人（含中专及五年制大专）。</p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("42","22","0","中州大学","/var/upload/image/2015/05/20150526163949_98338.png","http://","        中州大学（Zhongzhou University）是由郑州市人民政府创办的一所公办全日制综合性普通高等学校，创建于1980年，老一辈无产阶级革命家、政治家陈云同志亲笔为学校题写了校名。\r\n        截止2015年5月，学校占地面积1400余亩，设有17个教学机构，开设53个专业，涵盖交通运输、生化与药品、资源开发与测绘、材料与能源、土建、制造、电子信息、环保、气象与安全、轻纺食品、财经、旅游、公共事业、文化教育、艺术设计传媒等15个专业大类。[1] ","2015-05-19 11:10:48","0","1","0","0","109","","<p>中州大学（Zhongzhou University）是由郑州市人民政府创办的一所公办全日制综合性普通高等学校，创建于1980年，老一辈无产阶级革命家、政治家陈云同志亲笔为学校题写了校名。<br /> &nbsp; &nbsp; &nbsp; &nbsp;截止2015年5月，学校占地面积1400余亩，设有17个教学机构，开设53个专业，涵盖交通运输、生化与药品、资源开发与测绘、材料与能源、土建、制造、电子信息、环保、气象与安全、轻纺食品、财经、旅游、公共事业、文化教育、艺术设计传媒等15个专业大类。[1] <br /><br /></p>");
