INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("148","22","0","泰山学院","/var/upload/image/2015/05/20150529140144_83825.png","http://","泰山学院是2002年3月经教育部批准设立的一所全日制普通本科高校，自1958年开始举办高等教育，其办学历史可追溯到1905年（清光绪三十一年）创办的泰安府官立师范学堂。校园坐落在世界首例自然与文化双重遗产、世界地质公园——泰山南麓。\r\n截至2014年5月，学校占地1445亩，建筑面积52万平方米。学校设有17个二级学院，设置本科专业59个，涵盖文学、理学、工学、法学、历史学、教育学、管理学、经济学、艺术学等9大学科门类。学校面向全国27个省（市、自治区）招生，有全日制普通在校生1.8万人。","2015-05-29 13:57:15","0","1","0","0","116","","<p>泰山学院是2002年3月经教育部批准设立的一所全日制普通本科高校，自1958年开始举办高等教育，其办学历史可追溯到1905年（清光绪三十一年）创办的泰安府官立师范学堂。校园坐落在世界首例自然与文化双重遗产、世界地质公园——泰山南麓。</p><p>截至2014年5月，学校占地1445亩，建筑面积52万平方米。学校设有17个二级学院，设置本科专业59个，涵盖文学、理学、工学、法学、历史学、教育学、管理学、经济学、艺术学等9大学科门类。学校面向全国27个省（市、自治区）招生，有全日制普通在校生1.8万人。</p><p><br /></p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("149","22","0","河南科技学院","/var/upload/image/2015/05/20150529140255_93636.png","http://","河南科技学院（Henan Institute of Science and Technology）是一所省属普通本科院校，学校地处中原名城新乡市。始建于1949年，前身是北京农业大学长治分校和平原省立农业学校，历经平原农学院、百泉农业专科学校、河南职业技术师范学院等时期。2004年5月，经国家教育部批准，更名为河南科技学院，国家首批卓越农林人才教育培养计划改革试点高校。[1] \r\n截至2013年11月，学校占地面积2478亩，校舍面积62万平方米，设19个教学学院，58个本科专业，有各类在校生45000余人，教职工1600余人。","2015-05-29 14:02:14","0","1","0","0","117","","<p><br/></p><p>河南科技学院（Henan Institute of Science and Technology）是一所省属普通本科院校，学校地处中原名城新乡市。始建于1949年，前身是北京农业大学长治分校和平原省立农业学校，历经平原农学院、百泉农业专科学校、河南职业技术师范学院等时期。2004年5月，经国家教育部批准，更名为河南科技学院，国家首批卓越农林人才教育培养计划改革试点高校。</p><p>截至2013年11月，学校占地面积2478亩，校舍面积62万平方米，设19个教学学院，58个本科专业，有各类在校生45000余人，教职工1600余人。</p><p><br /></p>");
