INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("179","22","0","泰州科技大学","/var/upload/image/2015/06/20150604133637_99412.png","http://","南京理工大学泰州科技学院坐落于苏中美丽的凤凰城——江苏省泰州市大学城中部，是在国防科工委、江苏省委、省政府大力支持下，经教育部批准、由南京理工大学与泰州市政府合作举办的本科高等院校，是一所具备现代办学理念和科学育人模式的工科类独立学院， 被誉为“工程师的摇篮”。\r\n南京理工大学全面负责学院的教学与管理，毕业生颁发国家统一印制的南京理工大学泰州科技学院本科毕业证书，符合南京理工大学泰州科技学院学位授予条件的，授予南京理工大学泰州科技学院学士学位。","2015-06-04 13:35:54","0","1","0","0","120","","<p>南京理工大学泰州科技学院坐落于苏中美丽的凤凰城——江苏省泰州市大学城中部，是在国防科工委、江苏省委、省政府大力支持下，经教育部批准、由南京理工大学与泰州市政府合作举办的本科高等院校，是一所具备现代办学理念和科学育人模式的工科类独立学院， 被誉为“工程师的摇篮”。</p><p>南京理工大学全面负责学院的教学与管理，毕业生颁发国家统一印制的南京理工大学泰州科技学院本科毕业证书，符合南京理工大学泰州科技学院学位授予条件的，授予南京理工大学泰州科技学院学士学位。</p><p><br /></p>");
