INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("194","22","0","贵州师范学院","/var/upload/image/2015/06/20150608093645_87607.png","http://","贵州师范学院位于贵州省贵阳市乌当国家级高新技术产业园区，其前身系创建于1978年的贵州教育学院，2001年，贵阳职工大学并入学院，2009年3月经国家教育部批准改制为全日制普通本科师范院校。2013年学校有河滨、小河、乌当三个校区，学院占地面积1042亩（含代管林地350亩），校园建筑面积30余万平方米。截止2013年，现有全日制本专科在校生12402人，固定资产总值达4亿元，其中教学科研仪器设备总值6500多万元。截止2013年，学院有普通本科专业35个、普通专科专业9个，有省级特色重点学科3个、重点支持学科2个、省级科技创新团队1个、教育部全国专业综合改革试点专业2个，省级人文社科基地1个，省级重点实验室1个，厅级重点实验室2个。","2015-06-08 09:36:15","0","1","0","0","122","","<p>贵州师范学院位于贵州省贵阳市乌当国家级高新技术产业园区，其前身系创建于1978年的贵州教育学院，2001年，贵阳职工大学并入学院，2009年3月经国家教育部批准改制为全日制普通本科师范院校。2013年学校有河滨、小河、乌当三个校区，学院占地面积1042亩（含代管林地350亩），校园建筑面积30余万平方米。截止2013年，现有全日制本专科在校生12402人，固定资产总值达4亿元，其中教学科研仪器设备总值6500多万元。截止2013年，学院有普通本科专业35个、普通专科专业9个，有省级特色重点学科3个、重点支持学科2个、省级科技创新团队1个、教育部全国专业综合改革试点专业2个，省级人文社科基地1个，省级重点实验室1个，厅级重点实验室2个。<br /></p>");
