INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("198","23","0","我校与云岩区惠智学校签署教育实践基地协议","/var/upload/image/2015/06/20150608101258_49520.jpg","http://","6月3日上午，我校教育科学学院院长刘红、党委书记彭祥友、特殊教育教研室主任黎平辉等一行前往云岩区惠智学校签署教育实践基地协议，并举行教育实践基地挂牌仪式，同时看望了我校在此实习的2012级特殊教育专业学生。协议签订仪式由云岩区惠智学校校长张军主持，惠智学校全体教师参加。","2015-06-08 10:12:21","0","1","0","0","122","","<p style=\"text-align:center;\"><span style=\"font-size:24px;\">我校与云岩区惠智学校签署教育实践基地协议</span><br /></p><p> &nbsp; 6月3日上午，我校教育科学学院院长刘红、党委书记彭祥友、特殊教育教研室主任黎平辉等一行前往云岩区惠智学校签署教育实践基地协议，并举行教育实践基地挂牌仪式，同时看望了我校在此实习的2012级特殊教育专业学生。协议签订仪式由云岩区惠智学校校长张军主持，惠智学校全体教师参加。</p><p> &nbsp; 协议签订前，刘红、彭祥友等参加了云岩区惠智学校举办的庆“六一”活动，随行的我校特殊教育专业学生也做了精彩表演。随后，刘红等实地参观了云岩区惠智学校的儿童潜能开发实验室、康复训练室、模拟超市等办学设施。张军详细介绍了惠智学校的发展历史以及办学现状，表达了与我校特殊教育专业长期合作的良好愿望；刘红一行对惠智学校取得的办学成绩表示赞许，并希望通过高校与一线特教机构的合作，更好地培养特殊教育师资，以此促进贵州特殊教育的发展。</p><p> &nbsp; 协议签订后，刘红接受了相关媒体的采访，阐述了高校与一线特教机构合作的意义与价值，并表达了对贵州特殊教育发展的个人期许。此次活动，不仅扩大了我校特殊教育专业实践基地，为应用型人才培养夯实了基础，还有效宣传了贵州师范学院，提升了我校的社会知名度。</p><p style=\"text-align:center\"><img src=\"/ueditor/php/upload/20150608/14337296668423.jpg\" title=\"2kr74fjlfi.jpg\" /></p><p><br /></p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("202","23","0","我校教师杜宏博参加2015年贵州省教育工会“劳动模范上讲堂”活动","/var/upload/image/2015/06/20150608101839_85627.jpg","http://"," 5月29日，省教育工会在贵州师范大学宝山校区田家炳书院三楼多功能厅开展“劳动模范上讲堂”活动，来自全省各市、州教育工会、各直属学校（附院）工会领导及师大教师、学生代表230余人参加会议，贵州省教育工委委员、贵州省教育厅党组成员、省教育工会主席王慧作近期工作部署，贵州师范大学党委副书记银熙惠出席会议。","2015-06-08 10:17:56","0","1","0","0","122","","<p style=\"text-align:center;\"><span style=\"font-size:24px;\">我校教师杜宏博参加2015年贵州省教育工会“劳动模范上讲堂”活动</span><br /></p><p> &nbsp; 5月29日，省教育工会在贵州师范大学宝山校区田家炳书院三楼多功能厅开展“劳动模范上讲堂”活动，来自全省各市、州教育工会、各直属学校（附院）工会领导及师大教师、学生代表230余人参加会议，贵州省教育工委委员、贵州省教育厅党组成员、省教育工会主席王慧作近期工作部署，贵州师范大学党委副书记银熙惠出席会议。</p><p> &nbsp; 贵州师范大学社科处处长谢廷秋教授与大家分享了自己担任研究生处处长期间，与同事们发扬“咬定青山不放松、不达目的不罢休”的贵州教育精神，为学校成功申请博士学位授予点，为学校提升办学层次贡献了自己的力量。作为今年全省教育系统表彰最年轻的劳模，杜宏博老师以“激发学生的一颗好奇心”为题，把成为一名大学老师作为自己的梦想，2008年，他成为贵州师范学院物电学院的一名80后教师，为激发学生的学习兴趣，他将课堂教学模式转变为课堂教学+在线慕课+仿真设计+综合实验+微博互动的五维度教学模式；他还带领自己的团队先后申请了18项国家级、省级大学生创新项目，在全国机器人大赛中荣获多项国家级荣誉；团队7名同学有5名考上了复旦大学、电子科技大学、西南交通大学、西南大学、昆明理工大学的研究生，2名同学成功签约上海和深圳的app开发公司。两位老师用平实、生动的故事讲述了自己不同的故事，感染了在场的听众，与会师生对他们的报告给予热烈的掌声，并与两位老师进行了精彩的互动。</p><p> &nbsp; 王慧在总结讲话中要求：全省教育工会系统要高度重视，以学习劳模、宣传劳模为重点，精心知筹划、认真组织“建功立业大竞赛、和谐校园大创建、职工素质大提升、维权帮扶大推进、节能减排大参与”系列活动，在省内上下掀起学先进、颂劳模的新高潮。同时希望全省教育工作者注重工作创新、不断提高自身素质。在平凡的工作中创造伟大、创造奇迹。</p><p><br /></p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("203","23","0","贵州都市报：师范生找工作“离家近”成首选","/var/upload/image/2015/06/20150608102054_15745.jpg","http://","又到了每年的大学毕业生招聘季，记者在5月28日举办的贵州师范学院2015年校园大型招聘会上发现，是否离家近、发展空间大小成为师范类毕业生首先关注的问题。而且有别于往年的是，以往毕业生更愿意去学校，但当天现场很多毕业生更看好民办培训机构。","2015-06-08 10:20:39","0","1","0","0","122","","<p style=\"text-align:center;\"><span style=\"font-size:24px;\">贵州都市报：师范生找工作“离家近”成首选</span><br /></p><p> &nbsp; 贵州都市报在6月4日B14版，以题为“师范生找工作“离家近”成首选”报道了我校2015年校园大型招聘会，贵州都市网也进行了全文转载，全文如下：</p><p>本报记者段艳 摄影报道</p><p> &nbsp; 又到了每年的大学毕业生招聘季，记者在5月28日举办的贵州师范学院2015年校园大型招聘会上发现，是否离家近、发展空间大小成为师范类毕业生首先关注的问题。而且有别于往年的是，以往毕业生更愿意去学校，但当天现场很多毕业生更看好民办培训机构。</p><p>找工作 毕业生首选离家近</p><p> &nbsp; 张爱华是贵州师范学院2015届数学与计算机科学学院学生，5月28日，他和班上很多同学一起，一早就穿上干净整洁的衣服来到学校凤凰广场寻找自己心仪的工作。他们中很多同学的梦想都是当老师。</p><p> &nbsp;“我第一选择是看上班地点离家近不近、环境好不好。”招聘会现场，徐同学的求职目标很明确，他说，家在遵义，这次还是想选遵义的工作，其次选择贵阳，但是出省工作就完全不考虑了，唯一要求就是只要在家附近就可以了。</p><p>同样，一位英语专业的应届毕业生李同学表示，自己的家乡在安顺，最希望能在安顺或贵阳找到一份稳定的工作，如果与所学专业对口最好，而月工资3000元左右就可以接受。</p><p>随机采访中，记者问及多位毕业生“当前找工作优先考虑的因素”，受访者选择“离家近、发展空间大”的比例远高于其他因素，超过5成，另外选择“工作稳定”、“工作环境”等因素的比例也较高，均在2成以上。</p><p>不少90后求职者的想法更加现实。大多数人表示，希望找一份有发展前景的工作，工资每月两三千元就行，最重要的是能学到东西，有发展前景。</p><p>教育机构 毕业生也爱</p><p>“以往毕业生更愿意去学校，但从今年招聘的情况来看，很多毕业生更看好民办培训机构。”招聘会上，贵阳一家英语培训机构负责人介绍说，前几年，大学毕业生都想去学校，认为在学校当老师会更稳定一些，相对来说，培训机构工作强度大，同学们担心没有很好的保障。但从今年招聘的情况来看，很多毕业生更看好私立培训机构。</p><p>“其实教育培训机构也有自己的优势，比如接触面广、发展空间大、培训机制更灵活等”。该负责人表示，如今民办教育机构对很多非师范类毕业生的吸引力也不小，今年自己所在的培训机构主要招聘英语教师，最基本的要求是英语过6级。</p><p>现场，一家职业技术学校负责招聘的罗老师说，目前投了简历的学生，资格核实合格后，还要经过笔试、面试、课堂教学等环节考核才能被录用。同时，按照学校录用规定，对毕业生计算机、英语、普通话这些硬性条件也都有要求，想当老师语言表达能力、沟通能力也很重要。此外，应聘者还需要通过心理测试，需要有阳光的心态。</p><p>贵州师范学院招生就业指导处处长宋婷禹建议：师范类专业的学生，找工作可以把眼光放得更开阔一些，其实学校、教育机构，一些大型企业的人力资源部，对于师范类专业的学生也有较大需求。总体来说，面向应届毕业生的岗位不少，关键看毕业生愿不愿意干。</p><p>装饰行业也欢迎师范生“加盟”</p><p>另外还有一个特别值得关注的现象，此次校园招聘会共有近80家用人单位入场招聘，其中装饰相关企业占了近30%。主要招聘销售员、设计员、业务员和行政人员。</p><p>“公司这次的招聘岗位包括设计师、家装顾问、材料员、人事专员等。”贵阳一家装饰工程公司负责人介绍，设计师和家装规划等专业人才比较紧缺，为了应对市场变化，企业不得不加大自主培训的力度，并且尝试将其常态化。因为涉及培训，所以对于热爱装饰行业的师范生，公司也会非常欢迎。</p><p>另一家装饰公司贵州分公司的黄女士介绍说，公司招聘设计师、行政员、授课员、前台文员等，有经验者优先，待遇从高，其中设计师要求具有工作经历，能独当一面；设计助理、业务员，可以兼职。</p><p>她说，如今逛家装建材区的市民有不少是80后乃至90后，装修群体年轻化是趋势，这些年轻人眼界比较开阔，要求也比较高，给家装公司带来了不少新课题。很多人都是自己订购主材，再找装修公司帮忙设计和施工。所以企业招聘设计岗位都更看重创新和服务意识。</p><p><br /></p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("204","23","0","文学院“激情四‘摄’·‘影’领师院”摄影大赛","/var/upload/image/2015/06/20150608114332_58290.jpg","http://","6月1日，文学院“激情四摄·影领师院”摄影大赛颁奖典礼在宁静楼627高清演播室举行。文学院党委书记韦丹、广播电视教研室主任吕菁、广播电视教研室副教授贤娟出席颁奖典礼。","2015-06-08 11:43:06","0","1","0","0","122","","<p style=\"text-align:center;\"><span style=\"font-size:24px;\">文学院“激情四‘摄’·‘影’领师院”摄影大赛</span><br /></p><p> &nbsp; 6月1日，文学院“激情四摄·影领师院”摄影大赛颁奖典礼在宁静楼627高清演播室举行。文学院党委书记韦丹、广播电视教研室主任吕菁、广播电视教研室副教授贤娟出席颁奖典礼。在颁奖典礼上，韦丹书记发言表示，文学院一直是很支持这样活动的开展，也希望通过这样的活动来培养同学们的摄影兴趣并使得摄影技术有所提高，同时希望广播电视学和广播电视编导两个专业的同学能学以致用，在积极参加活动的过程中使自己的技能得到锻炼。</p><p> &nbsp; 本次摄影大赛由文学院13级广播电视学承办，在文学院团委的指导协助下开展具体工作，且在活动过程中得到了贤娟老师的全程指导，确保了大赛的圆满成功。大赛围绕我校迎评促建这一关键时刻，以摄影大赛这一独特方式将我校的美展示出来，以独特的视角记录师院风采，用镜头刻画了我们身边的精彩。大赛的宗旨是以艺术视角记录生活的瞬间，刻录情感世界，用镜头说话，通过积极健康、主题突出的摄影大赛展评活动来丰富师院学生课余生活，提高学生参与课外活动的积极性，激发学生们的创造性思维，增强同学们对摄影的认识，培养学生们的摄影能力，展示摄影独特的艺术魅力。</p><p> &nbsp; 此次大赛参赛作品共有222幅，其中艺术类197幅，新闻类25幅，所有参赛照片经过广播电视专业的老师及专家评选，共选出32幅以供展览，最后再从展览照片中评选出获奖作品，共评选出艺术类、新闻类一等奖各一名，二等奖各两名，三等奖各三名，优秀奖共10名。</p><p> &nbsp; 此次大赛不仅激发了摄影爱好者的摄影创作激情，活跃了其摄影创新思维，更给他们提供了沟通交流的机会、展示的平台。接下来，这些摄影爱好者将以更好的艺术视角记录生活。</p><p style=\"text-align:center\"><img src=\"/ueditor/php/upload/20150608/14337350827442.jpg\" title=\"2wtv0qglay.jpg\" /></p><p><br /></p>");
INSERT INTO `xsmart_ad`(`id`,`bid`,`classid`,`title`,`imgurl`,`http`,`description`,`times`,`recommend`,`audit`,`top`,`lmorder`,`orid1`,`orid2`,`content`) VALUES ("205","20","0","北京科技大学","/var/upload/image/2015/06/20150612023717_37603.jpg","http://","北京科技大学（University of Science &amp; Technology Beijing，USTB）是中华人民共和国教育部直属的一所以工科为主，工学、理学、管理学、文学、经济学、法学等多学科协调发展的全国重点大学，是国家“211工程”、“985工程优势学科创新平台”重点建设院校，入选“2011计划”、“卓越工程师教育培养计划”、“高等学校学科创新引智计划（111计划）“、“国家建设高水平大学公派研究生项目”，是北京高科大学联盟成员，设有研究生院。\r\n学校于1952年由北洋大学、清华大学、北京工业学院、西北工学院、唐山铁道学院、山西大学六所著名院校的矿冶学科组建而成，初名北京钢铁工业学院，1960年更名为北京钢铁学院，同年被列为首批全国重点大学。1988年，学校定名为北京科技大学。 \r\n截至2014年10月，全日制在校生2.7万余人，其中本专科生13393人，各类研究生9820人（其中博士生2912人、硕士生6908人），外国留学生829人。","2015-06-12 10:35:59","0","1","0","0","123","","<p>北京科技大学（University of Science &amp; Technology Beijing，USTB）是中华人民共和国教育部直属的一所以工科为主，工学、理学、管理学、文学、经济学、法学等多学科协调发展的全国重点大学，是国家“211工程”、“985工程优势学科创新平台”重点建设院校，入选“2011计划”、“卓越工程师教育培养计划”、“高等学校学科创新引智计划（111计划）“、“国家建设高水平大学公派研究生项目”，是北京高科大学联盟成员，设有研究生院。</p><p>学校于1952年由北洋大学、清华大学、北京工业学院、西北工学院、唐山铁道学院、山西大学六所著名院校的矿冶学科组建而成，初名北京钢铁工业学院，1960年更名为北京钢铁学院，同年被列为首批全国重点大学。1988年，学校定名为北京科技大学。 </p><p>截至2014年10月，全日制在校生2.7万余人，其中本专科生13393人，各类研究生9820人（其中博士生2912人、硕士生6908人），外国留学生829人。</p><p><br /></p>");
