INSERT INTO `xsmart_question_reply`(`id`,`qid`,`quid`,`requid`,`classid`,`sid`,`pid`,`coid`,`inputtime`,`istime`,`istrue`,`content`,`userType`,`realname`,`username`) VALUES ("53","21","39","41","","","","","1431428826","","0","<pre id=\"best-content-1706480082\" accuse=\"aContent\" class=\"best-text mb-10\" style=\"padding:0px;font-family:arial, \'courier new\', courier, 宋体, monospace;word-wrap:break-word;color:#333333;font-size:14px;line-height:24px;background-color:#F1FEDD;margin-top:0px;margin-bottom:10px;\">伴随网络搜索市场的急剧扩容，Google，<br>百度，微软MSN,Yahoo中国，网易等靠搜索竞价营销赢利的搜索公司，急需大量<br>具有丰富经验的网络营销人才。<br>前不久，作为阿里妈妈集团旗下专业从事网络广告业务的新公司， 阿里妈<br>妈开始向全国优秀网络营销人才伸出橄榄枝。据悉，阿里妈妈此次招聘人才达到数百人。<br>网络营销人才的需求，源自国内互联网市场的蓬勃发展。最新数字显示，<br>中国的网民人数已经超过美国，位居全球第一。根据权威调查机构iResearch 的<br>调查，受2008 年奥运会因素影响， 预计2008 年中国网络营销市场规模将突破<br>130 亿元。至2010 年，中国网络营销市场规模预计将达到230 亿元。<br>信息产业部电子人才交流中心李建伟处长表示：“目前大多数企业没有正<br>规专业的网络营销人员，一般都是由那些对专业市场很有经验，但对网络营销一<br>窍不通的传统营销人员，或是一些以技术为纲的网络技术人员客串网络营销人员<br>”。但是能靠网络赚钱的没有几个，一个关键原因在于行业专业的网络营销人才<br>匮乏。</pre><p><br></p>","2","","");
